
var video = document.getElementById("myVideo");
var btn = document.getElementById("myBtn");

function myFunction() {
  if (video.paused) {
    video.play();
    btn.innerHTML = "Pause";
  } else {
    video.pause();
    btn.innerHTML = "Play";
  }
}

$(document).ready(function(){
    $('.mpp').click(function() {
      $('#pp').show("slide");
      $('#pm').hide("slide");
      $('#os').hide("slide");
    });

    $('.mpm').click(function() {
      $('#pm').show("slide");
      $('#pp').hide("slide");
      $('#os').hide("slide");
    });
    $('.mos').click(function() {
      $('#os').show("slide");
      $('#pp').hide("slide");
      $('#pm').hide("slide");
    });
});
